mapper.update:
update contest_problem_user
set submit = submit + 1
where contest_id = #{contestId}
    and problem_number = #{problemNumber}
    and user_id = #{userId}

update contest_problem_user
set accept = accept + 1
where contest_id = #{contestId}
  and problem_number = #{problemNumber}
  and user_id = #{userId}

mapper.select:
select *
from contest_problem_user
where contest_id = #{contestId}
  and user_id = #{userId}
order by problem_number asc

select count(*)
from contest_problem_user
where contest_id = #{contestId}
  and problem_number = #{problemNumber}
  and submit > 0

select count(*)
from contest_problem_user
where contest_id = #{contestId}
  and problem_number = #{problemNumber}
  and accept > 0

select *
from contest_problem_user
where contest_id = #{contestId}
    and problem_number = #{problemNumber}
    and user_id = #{userId}
