mapper.update:
update problem
set submit = submit + 1
where id = #{id}

update problem
set test_set = 1
where id = #{id}

mapper.select:
select id, name, submit, accept
from problem
where author_id = #{authorId}
order by id desc
    limit #{startIndex}, #{pageSize}

select author_id
from problem
where id = #{id}

select count(*)
from problem
where author_id = #{authorId}

select count(*)
from problem
where id = #{id}
