mapper.update:
update record
set judge_result=#{judgeResult},
    execute_time=#{executeTime},
    execute_memory=#{executeMemory}
where id = #{id}
  and judge_result in ('PD', 'JD')

update record
set accepted_test_amount = accepted_test_amount + 1
where id = #{id}

update record
set compile_output = #{compileOutput}
where id = #{id}
    and compile_output is null

update record
set personal = #{personal}
where problem_id = #{problemId}
    and contest_id is null

update record
set personal = #{personal}
where problem_id = #{problemId}
  and contest_id is not null

mapper.select:
select *
from record
where username like concat('%', #{username}, '%')
    and contest_id is null

    and problem_id = #{problemId}


    and submit_language = #{submitLanguage}


    and judge_result = #{judgeResult}





                order by submit_time


                order by execute_time


                order by execute_memory



            desc



        order by id desc


limit #{startIndex}, #{pageSize}

select count(*)
from record
where username like concat('%', #{username}, '%')
    and contest_id is null

    and problem_id = #{problemId}


    and submit_language = #{submitLanguage}


    and judge_result = #{judgeResult}

select *
from record
where user_id = #{userId}
  and problem_id = #{problemId}
  and contest_id is null
order by id desc
    limit #{limit}

select *
from record
where username like concat('%', #{username}, '%')
    and contest_id = #{contestId}

    and problem_Number = #{problemNumber}


    and submit_language = #{submitLanguage}


    and judge_result = #{judgeResult}





                order by submit_time


                order by execute_time


                order by execute_memory



            desc



        order by id desc


limit #{startIndex}, #{pageSize}

select count(*)
from record
where username like concat('%', #{username}, '%')
    and contest_id = #{contestId}

    and problem_number = #{problemNumber}


    and submit_language = #{submitLanguage}


    and judge_result = #{judgeResult}

select *
from record
