mapper.update:
update record
set judge_result=#{judgeResult},
    execute_time=#{executeTime},
    execute_memory=#{executeMemory}
where id = #{id}
  and (judge_result = 'PD' or judge_result = 'JD')

update record
set accepted_test_amount = accepted_test_amount + 1
where id = #{id}

mapper.select:
select *
from record
where username like concat('%', #{username}, '%')

    and problem_id = #{problemId}


    and submit_language = #{submitLanguage}


    and judge_result = #{judgeResult}





                order by submit_time


                order by execute_time


                order by execute_memory



            desc



        order by id desc


limit #{startIndex}, #{pageSize}

select count(*)
from record
where username like concat('%', #{username}, '%')

    and problem_id = #{problemId}


    and submit_language = #{submitLanguage}


    and judge_result = #{judgeResult}

select *
from record
where user_id = #{userId}
  and problem_id = #{problemId}
order by id desc
    limit #{limit}
